mapper.update:
UPDATE reserve
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE reserve
SET scope=#{scope}, pwd=#{pwd}, shelterName=#{shelterName}, title=#{title}, reservedDate=#{reservedDate}, reservedTime=#{reservedTime},
name=#{name}, phoneNumber=#{phoneNumber}, content=#{content}
WHERE num=#{num}

UPDATE reserve
SET checked='yes'
WHERE num=#{num}

UPDATE reserve
SET checked='no'
WHERE num=#{num}

mapper.select:
SELECT pwd
FROM reserve
WHERE num=#{num}
